    //Saida
    nova_saida = new Date(saida);
    nova_saida = nova_saida.getDate()+`/`+( nova_saida.getMonth()+1 )+`/`+nova_saida.getFullYear()+' '+nova_saida.getHours()+':'+nova_saida.getMinutes();
